/*
 *  Specific test to ensure value stack is extended during CBOR decoding.
 *  This test is fragile: other tests in the same file may hide the possible
 *  bug because value stack may be extended during compilation!
 */

function dec(hexInput, expect) {
    var t = CBOR.decode(Duktape.dec('hex', hexInput));
    print(hexInput, Duktape.enc('jx', t), Duktape.enc('jx', expect));
}

/*===
 "" ""
 || ||
done
===*/

dec('7f' + ('60'.repeat(1024)) + 'ff', '');
dec('5f' + ('40'.repeat(1024)) + 'ff', new Uint8Array([]));

print('done');
